import s from 'styled-components'; //스타일 라이브러리 import
const Khbtn = s.button`
  background-color :red;
  border:none; color:#fff;
`;
/*
s.button 으로 버튼 생성함
백틱안에 인자값 담아서 함수호출 가능
Khbtn라는 변수를 컴포넌트처럼 활용 가능
*/

const Hello = () => {
  return <Khbtn>버튼입니당~</Khbtn>;
};

export default Hello;
